mapper.select:
SELECT count(*) as cnt
FROM ${table}

	
		${sel} like '%'||#{con}||'%'

SELECT * FROM (SELECT rownum as rnum, g.* FROM (SELECT * FROM member order by user_id desc) g)
user_id = #{id}
rnum >= #{startnum} AND rnum <= #{endnum}
AND ${sel} like '%'||#{con}||'%'

mapper.update:
